
public class Rectangle {
int length;
int width;
	public Rectangle(int length2 , int width2) {
		// TODO Auto-generated constructor stub

	length=length2;
	width=width2;
	
	
	
	}

	
	public int Area(){
		int Area=(length*width);
		return Area;
	}

public int Perimeter(){
int Perimeter=(2*(length)+2*(width));	
return Perimeter;	
	
}




}
